def clean_text(text):
    cleaned_text = re.sub(r'[^a-zA-Z0-9 /n/.]', '', text)
    
    text = cleaned_text

# create a new BeautifulSoup object
    soup = BeautifulSoup(text, 'lxml')

# remove all HTML tags
    text_without_html = soup.get_text()

# remove all URLs
    text_without_html_and_urls = re.sub(r'http\S+|www.\S+', '', text_without_html)

    print(text_without_html_and_urls)


clean_text(text)